const fs = require('fs');
const path = require('path');

// JSON 파일을 TypeScript 파일로 변환
function convertJsonToTs() {
  const jsonPath = path.join(__dirname, '../src/data/responsive-images.json');
  const tsPath = path.join(__dirname, '../src/data/responsive-images.ts');
  
  try {
    // JSON 파일 읽기
    const jsonData = fs.readFileSync(jsonPath, 'utf-8');
    
    // TypeScript 파일 내용 생성
    const tsContent = `// Auto-generated from responsive-images.json
// DO NOT EDIT THIS FILE MANUALLY

export const responsiveImagesManifest = ${jsonData} as const;

export default responsiveImagesManifest;
`;
    
    // TypeScript 파일 쓰기
    fs.writeFileSync(tsPath, tsContent, 'utf-8');
    
    console.log('✅ JSON을 TypeScript로 변환 완료: responsive-images.ts');
    
  } catch (error) {
    console.error('❌ 변환 실패:', error);
    process.exit(1);
  }
}

convertJsonToTs(); 